package ch02;

public class NameUnicode {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char a = '\uc131';
		char b = '\uc720';
		char c = '\uc11d';
		System.out.println("(\uc131)");
		System.out.println("char형(\'\\uc131'):"+a);
		System.out.println("char형(\'\\uc720'):"+b);
		System.out.println("char형(\'\\uc11d'):"+c);

	}

}
